import React, {useState, useEffect} from 'react';
import ReactSearchBox from 'react-search-box';

const SearchBar = (props) => {

  const [query, setQuery] = useState("");

  const handleChange = (event) =>{
      if(event.target.value != ""){
          props.requestQuery(event.target.value);
      }else props.requestQuery("");
  }
  
  const handleSubmit = (event) =>{
      event.preventDefault();
      props.menuStateChange(false);
  }

  const handleReset = (event) =>{
      props.requestQuery("");
  }
  
  return(
        
        <div className="form-group has-search" onSubmit={handleSubmit}>
            <svg class="bi bi-search" width="1em" height="1em" viewBox="0 0 10 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                <path fill-rule="evenodd" d="M10.442 10.442a1 1 0 011.415 0l3.85 3.85a1 1 0 01-1.414 1.415l-3.85-3.85a1 1 0 010-1.415z" clip-rule="evenodd"/>
                <path fill-rule="evenodd" d="M6.5 12a5.5 5.5 0 100-11 5.5 5.5 0 000 11zM13 6.5a6.5 6.5 0 11-13 0 6.5 6.5 0 0113 0z" clip-rule="evenodd"/>
            </svg>
            <input type="text" className="form-control" placeholder="Search" />
        </div>
        
        
    );
}

export default SearchBar;
