import React, {useState, useEffect} from 'react';
import ReactSearchBox from 'react-search-box';

const SearchBar = (props) => {

  const [query, setQuery] = useState("");

  const handleChange = (event) =>{
      if(event.target.value != ""){
          props.requestQuery(event.target.value);
      }else props.requestQuery("");
  }
  
  const handleSubmit = (event) =>{
      event.preventDefault();
      props.menuStateChange(false);
  }

  const handleReset = (event) =>{
      props.requestQuery("");
  }
  
  return(
    
        <div className="input-group" onSubmit={handleSubmit}>
            <span className="input-group-prepend">
                <button className="btn btn-light" type="button">
                    <i className="fa fa-search"></i>
                </button>
            </span>
            <input className="form-control" type="text" value="" id="example-search-input" />
        </div>
        
    );
}

export default SearchBar;
